import React from 'react';
import logo from './logo.svg';
import './App.css';

const CategoryList = props => 
  <div>
  {props.categoryTitle}
  </div>

class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {category: [], categoryID: 0, categoryTitle: ""}
  }

  add_category(){
    this.setState({
      categoryID: this.categoryID + 1,
      category: [...this.state.category, {item: this.state.categoryTitle, categoryID: this.state.categoryID+1}],
      categoryTitle:""
    })
    alert(this.state.categoryTitle)
  }


  render(){
    return(
        <div>
        <div>TRACK YOUR LIFE</div>
        <input type="text" 
          onChange={(event) => this.setState({categoryTitle: event.target.value})}
          value={this.state.categoryTitle}/>
        <div onClick={this.add_category.bind(this)}>add!</div> 
        <div>
          {this.state.category.map(info => 
            <CategoryList {...info}/>)}
        </div>     
      </div>
      )
  }
}

export default App;
