import React from 'react';
import logo from './logo.svg';
import './App.css';

class category{
  constructor(cat_title, cat_ID, item_list){
    this.categoryTitle = cat_title;
    this.catgoryID = cat_ID;
    this.itemList = item_list;
  }
}

class itemList{
  constructor(itemList_ID, items=[], item_num, item_desc, item_p, item_due){
    this.itemListID = itemList_ID;
    this.itemDesc = item_desc;
    this.itemPriority = item_p;
    this.itemDue = item_due;
    this.item = items;
    this.total_item = item_num; //track how many items in the item list
  }
}
class item{
  constructor(item_desc, item_p, item_due, item_id){
    this.itemDesc = item_desc;
    this.itemPriority = item_p;
    this.itemDue = item_due;
    this.itemID = item_id;
  }
}
class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {category_list: [], categoryListID: 0, categoryTitle: "", itemDesc: "", itemPriority: "", itemDue: "", item_num: 0}
    this.add_category = this.add_category.bind(this);
  }

  add_category(){
    let cat_title = this.state.categoryTitle;
    let catid = this.state.categoryListID;
    let item_content = this.state.itemDesc;
    let item_priority = this.state.itemPriority;
    let item_due = this.state.itemDue;
    let itemNum = this.state.item_num;
    let item_list = new itemList(catid, [], itemNum, item_content, item_priority, item_due);
    let listofitems = item_list.item;
    let category_item = new category(cat_title, catid, [item_list], itemNum);
    this.setState({
      categoryID: this.categoryListID + 1,
      category_list: [...this.state.category_list, category_item],
      categoryTitle: ""
    })
    alert(listofitems);
  }

  add_item(itemList, cid){
    let modified_itemlist = itemList.find(({itemListID, item, total_item}) => itemListID == cid);
    let items = modified_itemlist.item;
    let item_id = modified_itemlist.total_item + 1;
    let item_content = this.state.itemDesc;
    let item_priority = this.state.itemPriority;
    let item_due = this.state.itemDue;
    let new_item = new item(item_content, item_priority, item_due, item_id);
    this.setState({
      itemDesc: "",
      itemPriority: "",
      itemDue: "",
      items: [...items, new_item]
    })
    alert(items.length);
  }



  render(){
    return(
      <div>
        <div>TRACK YOUR LIFE</div>
        <input type="text" 
          onChange={(event) => this.setState({categoryTitle: event.target.value})}
          value={this.state.categoryTitle}/>
        <div onClick={this.add_category}>add!</div>
        <div>
        {this.state.category_list.map(
          ({categoryTitle, categoryID, itemList}) =>
            <li key = {categoryID}>
            {categoryTitle} - 
            <input type="text"
                  onChange={(event) => this.setState({itemDesc: event.target.value})}
                  value={this.state.itemDesc}/>
                <input type="text"
                  onChange={(event) => this.setState({itemPriority: event.target.value})}
                  value={this.state.itemPriority}/>
                <input type="text"
                  onChange={(event) => this.setState({itemDue: event.target.value})}
                  value={this.state.itemDue}/>
                <span onClick={this.add_item.bind(this, itemList, categoryID)}>New item</span>

            </li>
          )}
        </div>  
      </div>
      )
  }
}


export default App;
