import React from 'react';
import logo from './logo.svg';
import './App.css';

class item extends React.Component{
  constructor(props){
    super(props)
    this.state = {item: [], itemID: 0, itemDesc: "", itemPriority: "", itemDue: "", itemSectionID: 0}
  }
  add_item(category_id){
    this.setState({
      itemSectionID: category_id,
      item: [...this.state.item, {itemDesc: this.state.itemDesc, itemPriority: this.state.itemPriority, itemDue: this.state.itemDue}],
      itemID: this.state.itemID + 1,
      itemDesc: "",
      temPriority: "",
      itemDue: "",
    })
    alert(this.state.itemSectionID);
  }
}
class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {category: [], categoryID: 0, categoryTitle: ""}
  }

  add_category(){
    this.setState({
      categoryID: this.categoryID + 1,
      category: [...this.state.category, {categoryTitle: this.state.categoryTitle, categoryID: this.state.categoryID+1}],
      categoryTitle: ""
    })
    alert(this.state.categoryTitle)
  }


  render(){
    return(
      <div>
        <div>TRACK YOUR LIFE</div>
        <input type="text" 
          onChange={(event) => this.setState({categoryTitle: event.target.value})}
          value={this.state.categoryTitle}/>
        <div onClick={this.add_category.bind(this)}>add!</div>
        <ul>
          {this.state.category.map( 
            ({categoryTitle,categoryID}) => 
              <li key={categoryID}>
                {categoryTitle} - 

              </li> )}
        </ul>  
      </div>
      )
  }
}


export default App;
