import React from 'react';
import logo from './logo.svg';
import './App.css';

class category extends React.Component{
  constructor(cat_title, cat_ID, item_list){
    this.categoryTitle = cat_title;
    this.catgoryID = cat_ID;
    this.itemList = item_list;
  }
}

class itemList extends React.Component{
  constructor(itemList_ID, item=[]){
    this.itemListID = itemList_ID;
    this.item = item;
  }
}
class item extends React.Component{
  constructor(props){
    super(props);
    this.state = {itemID: 0, itemDesc: "", itemPriority: "", itemDue: "", itemListID: 0};
    this.add_item = this.add_item.bind(this);
  }
}
class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {category_list: [], categoryListID: 0, categoryTitle: "" };
    this.add_category = this.add_category.bind(this)
  }

  add_category(){
    let cat_title = this.state.categoryTitle;
    let catid = this.state.categoryListID;
    let item_list = new itemList(catid, []);
    let category = new category(cat_title, catid, item_list);
    this.setState({
      categoryID: this.categoryListID + 1,
      category_list: [...this.state.category_list, category],
      categoryTitle: ""
    })
    alert(this.state.categoryTitle)
  }



  render(){
    return(
      <div>
        <div>TRACK YOUR LIFE</div>
        <input type="text" 
          onChange={(event) => this.setState({categoryTitle: event.target.value})}
          value={this.state.categoryTitle}/>
        <div onClick={this.add_category}>add!</div>
        <div>
        {this.state.category_list.map(
          ({categoryTitle, categoryID, itemList}) =>
            <li key = {categoryID}>
            {categoryTitle}
            </li>
          )}
        </div>  
      </div>
      )
  }
}


export default App;
