import React from 'react';
import logo from './logo.svg';
import './App.css';

class category{
  constructor(cat_title, cat_ID, item_list=[]){
    this.categoryTitle = cat_title;
    this.categoryID = cat_ID;
    this.item_List = item_list;
  }
}

class item{
  constructor(itemList_ID, item_num, item_desc, item_p, item_due){
    this.itemListID = itemList_ID;
    this.itemDesc = item_desc;
    this.itemPriority = item_p;
    this.itemDue = item_due;
    this.total_item = item_num; //track how many items in the item list
  }
}

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {category_list: [], categoryListID: 0, categoryTitle: "", itemDesc: "", itemPriority: "", itemDue: ""}
    this.add_category = this.add_category.bind(this);
  }

  add_category(){
    let cat_title = this.state.categoryTitle;
    let catid = this.state.categoryListID;
    let category_item = new category(cat_title, catid, []);
    this.setState({
      categoryListID: this.state.categoryListID + 1,
      category_list: [...this.state.category_list, category_item],
      categoryTitle: ""
    })
    alert(this.state.categoryListID);
  }

  add_item(cid){
    alert("which cat belongs to " + cid);
    let modified_itemlist = this.state.category_list.find(({categoryTitle, categoryID, item_List}) => categoryID === cid);
    let items = modified_itemlist.item_List;
    let item_id = items.length + 1;
    let item_content = this.state.itemDesc;
    let item_priority = this.state.itemPriority;
    let item_due = this.state.itemDue;
    let new_item = new item(cid, item_id, item_content, item_priority, item_due);
    items = [...items, new_item];
    this.setState({
      itemDesc: "",
      itemPriority: "",
      itemDue: ""
    })
    //alert(item_id);
    modified_itemlist.item_List = items;
    //alert(modified_itemlist.item_List.length);

  }

  delete_item(delid, cid){
      let cat = this.state.category_list;
      let cat_index = this.state.category_list.findIndex(({categoryTitle, categoryID, item_List}) => categoryID === cid);
      let deleted_itemlist = this.state.category_list.find(({categoryTitle, categoryID, item_List}) => categoryID === cid);
      let deleted_item = deleted_itemlist.item_List.filter(({itemListID,itemDesc, itemPriority, itemDue, total_item}) => delid !== total_item);
      // let item_index = deleted_itemlist.item_List.findIndex( ({itemListID,itemDesc, itemPriority, itemDue, total_item}) => delid === total_item);
      // let deleted_item = deleted_itemlist.item_List.splice(item_index, 1);
      deleted_itemlist.item_List = deleted_item;
      //this.state.category_list[cat_index] = deleted_itemlist;
      cat[cat_index] = deleted_itemlist;
      //alert(deleted_itemlist);
      this.setState({
        category_list: cat
      })
      //alert("total length of itemlist" + cat[cat_index].item_List.length);


  }



  render(){
    return(
      <div>
        <div className="header">TRACK YOUR LIFE</div>
        <input type="text" 
          onChange={(event) => this.setState({categoryTitle: event.target.value})}
          value={this.state.categoryTitle}/>
        <div onClick={this.add_category}>add!</div>
        <div className="content">

        {this.state.category_list.map(Newcat =>
            <div key={Newcat.categoryID}>

            {Newcat.categoryTitle}


            <input type="text"
                  onChange={(event) => this.setState({itemDesc: event.target.value})}
                  value={this.state.itemDesc}/>
            <input type="text"
                  onChange={(event) => this.setState({itemPriority: event.target.value})}
                  value={this.state.itemPriority}/>
            <input type="text"
                  onChange={(event) => this.setState({itemDue: event.target.value})}
                  value={this.state.itemDue}/>
            <button onClick={this.add_item.bind(this, Newcat.categoryID)}>NewItem</button> 

            {Newcat.item_List.map(Newitem =>
                  <div key={Newitem.total_item}>
                  {Newitem.itemDesc} - {Newitem.itemPriority} - {Newitem.itemDue}
                  <button onClick={this.delete_item.bind(this, Newitem.total_item, Newcat.categoryID)}>Delete item </button>
                  </div>
                  )}

            </div>
        )}

        </div>  
      </div>
      )
  }
}


export default App;
